INSERT INTO `wpsd_revslider_navigations` VALUES
(1, 'Supro Nav Dark', 'supro-nav-dark', 'arrows', '.supro-nav-dark.tparrows {\n	background: transparent;\n    width: 70px\n}\n\n.supro-nav-dark:before {\n	display: none;\n}\n\n.supro-nav-dark .supro-arrow {\n	color: #222222;\n    position: absolute;\n    top: 50%;\n    left: 50%;\n    transform: translate(-50%,-50%);\n    transition: 0.5s;\n    width: 100%;\n    text-align: left;\n    display: flex;\n    align-items: center;\n    justify-content: space-between;\n}\n\n.supro-nav-dark .supro-arrow.right-arrow {\n	text-align: right;\n} \n\n.supro-nav-dark i {\n	font-size: 30px;\n}\n\n.supro-nav-dark .arrow-label {\n	font-size: 12px;\n    font-weight: 700;\n    opacity: 0;\n    transition: 0.5s;\n    display: inline-block;\n}\n\n.supro-nav-dark:hover .arrow-label {\n	opacity: 1;\n}\n\n.supro-nav-dark.tp-leftarrow .right-arrow{\n	display: none;\n}\n\n.supro-nav-dark.tp-rightarrow .left-arrow{\n	display: none;\n}', '<div class=\\"supro-arrow right-arrow\\">\n	<span class=\\"arrow-label\\">NEXT</span>\n	<i class=\\"icon-arrow-right\\"></i>\n</div>\n<div class=\\"supro-arrow left-arrow\\">\n	<i class=\\"icon-arrow-left\\"></i>\n    <span class=\\"arrow-label\\">PREV</span>\n</div>\n', '{"dim":{"width":"160","height":"160"},"placeholders":{},"presets":{},"version":"6.0.0"}'),
